BEGIN TRANSACTION;

CREATE TABLE IF NOT EXISTS "projects" (
	"id"	        INTEGER PRIMARY KEY AUTOINCREMENT,
	"name"	        TEXT,
	"description"	TEXT,
	"date"			TEXT,
	"team"			TEXT
);

CREATE TABLE IF NOT EXISTS "questions" (
	"id"	        INTEGER PRIMARY KEY AUTOINCREMENT,
	"title"	        TEXT,
	"description"	TEXT,
	"userid"		INTEGER,
	"projectid"		INTEGER,
	"date"			TEXT
);

CREATE TABLE IF NOT EXISTS "solutions" (
	"id"	        INTEGER PRIMARY KEY AUTOINCREMENT,
	"text"	        TEXT,
	"userid"		INTEGER,
	"questionid"	INTEGER,
	"nlikes"		INTEGER,
	"ndislikes"		INTEGER,
	-- liked -> 0 not liked or disliked, 1 liked, 2 disliked
	"liked"			INTEGER,
	"date"			TEXT
);

CREATE TABLE IF NOT EXISTS "users" (
	"id"	    INTEGER PRIMARY KEY AUTOINCREMENT,
	"name"	    TEXT,
	"email"		TEXT,
	"hash"		TEXT,
	"salt"		TEXT
);








COMMIT;

